import { Component, OnInit, ViewChild } from '@angular/core';
import { ModalDismissReasons, NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { Observable, ReplaySubject } from 'rxjs';
import { FormTitleHd } from 'src/app/modules/models/formTitleHd';
import { DbCommonService } from 'src/app/modules/_services/db-common.service';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';
import { ReferenceDetailsService } from 'src/app/modules/_services/reference-details.service';
import { ToastrService } from 'ngx-toastr';
import { SelectRefTypeDto } from 'src/app/modules/models/ReferenceDetails/SelectRefTypeDto';
import { RefTableDto } from 'src/app/modules/models/ReferenceDetails/RefTableDto';
import { MatTableDataSource } from '@angular/material/table';
import { MatPaginator } from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';
import { CommonService } from 'src/app/modules/_services/common.service';

@Component({
  selector: 'app-reference-details',
  templateUrl: './reference-details.component.html',
  styleUrls: ['./reference-details.component.scss']
})
export class ReferenceDetailsComponent implements OnInit {


  //#region 
  /*----------------------------------------------------*/

  // Language Type e.g. 1 = ENGLISH and 2 =  ARABIC
  languageType: any;

  // Selected Language
  language: any;

  // We will get form lables from lcale storage and will put into array.
  AppFormLabels: FormTitleHd[] = [];

  // We will filter form header labels array
  formHeaderLabels: any[] = [];

  // We will filter form body labels array
  formBodyLabels: any[] = [];

  // FormId
  formId: string;

  /*----------------------------------------------------*/
  //#endregion

  //
  refSubType$: any;
  //
  refType$: Observable<SelectRefTypeDto[]>;
  //


  //#region
  // To display table column headers
  columnsToDisplay: string[] = ['action', 'Sr', 'shortName', 'englishName1', 'arabicName2'];

  // Getting data as abservable.
  refTableDto$: Observable<RefTableDto[]>;

  // We need a normal array of data so we will subscribe to the observable and will get data
  refTableDto: MatTableDataSource<RefTableDto> = new MatTableDataSource<any>([]);

  // Paginator
  @ViewChild(MatPaginator) paginator!: MatPaginator;

  // Sorting
  @ViewChild(MatSort) sort!: MatSort;

  // Hide footer while loading.
  isLoadingCompleted: boolean = false;

  // Incase of any error will display error message.
  dataLoadingStatus: string = '';

  // True of any error
  isError: boolean = false;

  // formGroup
  formGroup: FormGroup;

  // Search Term
  searchTerm: string = '';

  //#endregion

  lang: string;

  closeResult: string = '';

  //
  currentPage = 0;
  totalRows = 0;
  pageSizeOptions: number[] = [10, 20, 50, 100];
  reftableHeaders: any = {};

  //
  userForm: FormGroup;
  //
  userEditForm: FormGroup;
  // 
  isFormSubmitted = false;
  //
  selectedItems: any = [];
  selectedItemsSub: any = [];

  // Will disable Add button if RefSubType not selected
  isRefSubTypeEmpty = false;

  length: number;
  pageSize: number = 10;

  constructor(private modalService: NgbModal,
    private commonService: DbCommonService,
    private common: CommonService,
    private fb: FormBuilder,
    private refTableService: ReferenceDetailsService,
    private toastrService: ToastrService) {
    this.formGroup = new FormGroup({
      searchTerm: new FormControl("")
    })
    this.refSubType$ = new ReplaySubject(1);
  }


  ngOnInit(): void {
     
    this.common.getLang().subscribe((lang: string) => {
      this.lang = lang;
    })
    //#region TO SETUP THE FORM LOCALIZATION    
    // TO GET THE LANGUAGE ID e.g. 1 = ENGLISH and 2 =  ARABIC
    this.languageType = localStorage.getItem('langType');

    // To get the selected language...
    this.language = localStorage.getItem('lang');

    // To setup the form id so will will get form labels based on form Id
    this.formId = 'RefTableEntry';

    // Check if LocalStorage is Not NULL
    if (localStorage.getItem('AppLabels') != null) {
      
      // Get data from LocalStorage
      this.AppFormLabels = JSON.parse(localStorage.getItem('AppLabels') || '{}');

      for (let labels of this.AppFormLabels) {

        if (labels.formID == this.formId) {

          this.formHeaderLabels.push(labels);

          const jsonFormTitleDTLanguage = labels.formTitleDTLanguage.reduce((result: any, element) => {
            result[element.labelId] = element;
            return result;
          }, {})
          this.formBodyLabels.push(jsonFormTitleDTLanguage);
        }
      }
      console.log(this.formBodyLabels)
      console.log(this.formHeaderLabels)
    }
    //#endregion

    // Filling RefType...
    this.refType$ = this.commonService.GetRefTypes()
    //
    //this.loadData(0);
    this.initializeForm();
    //
    this.initializeEditForm();

  }


  onUserFormSubmit() {
    // Get Tenant Id
    var data = JSON.parse(localStorage.getItem("user")!);
    const tenantId = data.map((obj: { tenantId: any; }) => obj.tenantId);

    //  TO CONVER OBJECT ARRAY AS SIMPLE ARRAY. 
    let formData = {
      ...this.userForm.value,
      tenentID: tenantId[0], cruP_ID: 0
    }

    //Add New record
    this.refTableService.AddRefTable(formData).subscribe(response => {
      if (response === 500) {
        this.toastrService.error('Something went wrong. please again latter', 'Error');
      } else {
        this.toastrService.success('Saved successfully', 'Success');
        this.isFormSubmitted = false;
        this.userForm.reset();

      }
    });
  }

  initializeForm() {
    this.userForm = this.fb.group({
      refType: new FormControl('', Validators.required),
      refSubType: new FormControl('', Validators.required),
      shortName: new FormControl('', [Validators.required, Validators.email]),
      refname2: new FormControl('', Validators.required),
      refname3: new FormControl('', Validators.required),
      switch1: new FormControl('', Validators.required),
      switch2: new FormControl('', Validators.required),
      switch3: new FormControl('', Validators.required),
      active: new FormControl('', Validators.required),
      infrastructure: new FormControl('', Validators.required),
      remarks: new FormControl('', Validators.required),
      refImage: new FormControl('', Validators.required),
    })
  }

  initializeEditForm() {
    this.userEditForm = this.fb.group({
      refid: new FormControl('', Validators.required),
      refType: new FormControl('', Validators.required),
      refSubType: new FormControl('', Validators.required),
      refname1: new FormControl('', Validators.required),
      refname2: new FormControl('', Validators.required),
      refname3: new FormControl('', Validators.required),
      remarks: new FormControl('', Validators.required),
      active: new FormControl('', Validators.required),
      refImage: new FormControl('', Validators.required),
    })
  }

  onRefSubTypeChange($event: any) {
    this.userForm.value.refSubType = $event
    this.selectedItemsSub = $event;
    this.isRefSubTypeEmpty = true;
    this.loadData(0);
    this.initializeForm();
  }

  onRefTypeChange($event: any) {
    this.selectedItems = $event;
    this.commonService.GetRefSubTypeByRefType($event.refType).subscribe((response) => {
      this.refSubType$ = response
    })
  }
  // Reset form
  resetForm() {
    this.userForm.reset();
  }

  // Load data...
  loadData(pageIndex: any) {
    this.refTableService.GetAllRefTableRecordsByRefTypeAndSubType(pageIndex + 1, this.pageSize, this.selectedItems.refType, this.selectedItemsSub.refSubType, this.formGroup.value.searchTerm).subscribe((response: any) => {
      console.log(response);
      this.refTableDto = new MatTableDataSource<any>(response.body.refTableDto);
      this.refTableDto.paginator = this.paginator;
      this.refTableDto.sort = this.sort;
      this.isLoadingCompleted = true;
      setTimeout(() => {
        this.paginator.pageIndex = pageIndex;
        this.paginator.length = response.body.totalRecords;
      });
    }, error => {
      console.log(error);
      this.dataLoadingStatus = 'Error fetching the data';
      this.isError = true;
    })
  }

  onPaginationChange(event: any) {
    this.pageSize = event.pageSize;
    this.loadData(event.pageIndex);
  }
  // navigateToVoucherDetails(voucherId: number) {
  //   this.router.navigateByUrl(`/service-setup/voucher-details?voucherId=${voucherId}`);
  // }
  // redirectTo(uri: string) {
  //   this.router.navigateByUrl('/', { skipLocationChange: true }).then(() =>
  //     this.router.navigate([uri]));
  // }
  //#region Material Search and Clear Filter 
  filterRecords(pageIndex: number = -1) {
    if (this.formGroup.value.searchTerm != null && this.refTableDto) {
      this.formGroup.value.searchTerm = this.refTableDto.filter = this.formGroup.value.searchTerm.trim();
    }
    if (pageIndex == 0) this.loadData(0);
    else this.loadData(this.paginator.pageIndex);
  }
  clearFilter() {
    this.formGroup?.patchValue({ searchTerm: "" });
    this.filterRecords();
  }
  //#endregion

  // Delete recored...
  openDeleteModal(content: any, id: number) {
    this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title' }).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
      if (result === 'yes') {
        console.log(id);
        this.refTableService.DeleteRefTable(id).subscribe(response => {
          if (response === 1) {
            this.toastrService.success('Record deleted successfully', 'Success');
            // Refresh Grid
            this.loadData(0);
          } else {
            this.toastrService.error('Something went wrong', 'Errro');
          }
        });
      }
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });

  }

  open(content: any) {
    this.userForm.patchValue({
      refType: this.selectedItems?.refType,
      refSubType: this.selectedItemsSub?.refSubType
    })
    this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title', modalDialogClass: 'modal-xl' }).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;

    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }
  // To update the existing record...
  openEditModal(content: any, id: number) {
    this.userEditForm.patchValue({
      refType: this.selectedItems?.refType,
      refSubType: this.selectedItemsSub?.refSubType,
      refid: id
    })
    this.refTableService.GetRefTableRecordsByIdRefTypeAndSubType(this.userEditForm.get('refid')?.value, this.userEditForm.get('refType')?.value, this.userEditForm.get('refSubType')?.value).subscribe((res: any) => {
      this.userEditForm.patchValue({
        refType: this.selectedItems?.refType,
        refSubType: this.selectedItemsSub?.refSubType,
        refid: id,
        refname1: res.refname1,
        refname2: res.refname2,
        refname3: res.refname3,
        remarks: res.remarks
      })
    })
    this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title', modalDialogClass: 'modal-xl' }).result.then((result) => {
      this.closeResult = `Closed with: ${result}`;

    }, (reason) => {
      this.userEditForm.reset();
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }
  onEditFormSubmit() {
    this.refTableService.UpdateRefTable(this.userEditForm.value).subscribe(response => {
      if (response === 500) {
        this.toastrService.error('Something went wrong. please again latter', 'Error');
      } else {
        this.toastrService.success('Updated successfully', 'Success');
        this.resetEditForm();

      }
    });
  }

  resetEditForm() {
    this.userEditForm.reset();
  }
  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else {
      return `with: ${reason}`;
    }
  }
}
<!-- TO GET FORM ID AND SETTINGUP LANGAUGE. -->
<input type="hidden" #RefTableEntry name="RefTableEntry" value="RefTableEntry">
<div class="card mb-5 mb-xl-8">
  <!--begin::Header-->
  <div class="card-header border-0 pt-5 setup-bg-header">
    <!--<div class="card-toolbar" *ngFor="let label of formHeaderLabels; let i = index">-->
    <h3 class="text-dark ArFontStyle theader" lang="AR-SA" id="RefTableEntry">{{ lang == 'en' ? formHeaderLabels[0].headerName : formHeaderLabels[1].headerName}}</h3>
    <!--</div>-->

  </div>
  <!--end::Header-->
  <!--begin::Body-->
  <div class="card-body py-3">
    <div class="row mb-4">
      <div class="col-md-4">
        <label class="form-label" for="form3Example1">{{ lang == 'en' ? formBodyLabels[0].lblReferenceType.title : formBodyLabels[1].lblReferenceType.title}}</label>
        <ng-select [items]="refType$ | async" bindLabel="refType"
                   (change)="onRefTypeChange($event)">
        </ng-select>
      </div>
      <div class="col-md-4">
        <label class="form-label" for="form3Example2">{{ lang == 'en' ? formBodyLabels[0].lblReferenceSubType.title : formBodyLabels[1].lblReferenceSubType.title}}</label>
        <ng-select [items]="refSubType$" bindLabel="refSubType" [multiple]="false"
                   (change)="onRefSubTypeChange($event)">
        </ng-select>
      </div>
      <div class="col-md-4">
        <button mat-raised-button color="primary" [disabled]="!isRefSubTypeEmpty" (click)="open(mymodal)"
                style="margin-top: 30px">
          {{ lang == 'en' ? formBodyLabels[0].lblAddRefTable.title : formBodyLabels[1].lblAddRefTable.title}}
        </button>
      </div>
    </div>
    <form [formGroup]="formGroup">
      <mat-form-field appearance="fill" class="w-100" floatLabel="never">
        <mat-label>{{ lang == 'en' ? formBodyLabels[0].lblSearch.title : formBodyLabels[1].lblSearch.title}}</mat-label>
        <input type="search" matInput formControlName="searchTerm" (keyup.enter)="filterRecords(0)" class="ArFontStyle" />
        <!-- <mat-icon matPrefix class="material-icons">{{ lang == 'en' ? formBodyLabels[0].lblSearch.title : formBodyLabels[1].lblSearch.title}}</mat-icon> -->
        <mat-icon matSuffix (click)="clearFilter()" class="material-icons">close</mat-icon>
      </mat-form-field>
    </form>
    <div class="table-container">
      <table mat-table class="w-100" [dataSource]="refTableDto" matSort>
        <ng-container matColumnDef="action">
          <th mat-header-cell *matHeaderCellDef class="ArFontStyle">{{ lang == 'en' ? formBodyLabels[0].lblAction.title : formBodyLabels[1].lblAction.title}}</th>
          <td mat-cell *matCellDef="let item" class="w-10 text-right">
            <div *ngIf="item.infrastructure == 'Y'">
              <button mat-raised-button color="warn" [disabled]="true" (click)="openDeleteModal(deleteModal,item.refid)">Delete</button>
              &nbsp; <button mat-raised-button color="accent" (click)="openEditModal(editModal,item.refid)">Edit</button>
            </div>
            <div *ngIf="item.infrastructure == 'N'">
              <button mat-raised-button color="warn" (click)="openDeleteModal(deleteModal,item.refid)">Delete</button>
              &nbsp; <button mat-raised-button color="accent" (click)="openEditModal(editModal)">Edit</button>
            </div>
          </td>
        </ng-container>
        <ng-container matColumnDef="Sr">
          <th mat-header-cell *matHeaderCellDef mat-sort-header class="w-10 ArFontStyle">
            {{ lang == 'en' ? formBodyLabels[0].lblRefMainColumn.title : formBodyLabels[1].lblRefMainColumn.title}}
          </th>
          <td mat-cell *matCellDef="let item" class="w-10 ArFontStyle">
            {{item.refid }} {{item.reftype }}
            {{item.refsubtype }}
          </td>
        </ng-container>
        <ng-container matColumnDef="shortName">
          <th mat-header-cell *matHeaderCellDef mat-sort-header class="ArFontStyle">
            {{ lang == 'en' ? formBodyLabels[0].lblShortName.title : formBodyLabels[1].lblShortName.title}}
          </th>
          <td mat-cell *matCellDef="let item" class="w-10 ArFontStyle">{{item.shortname}}</td>
        </ng-container>
        <ng-container matColumnDef="englishName1">
          <th mat-header-cell *matHeaderCellDef mat-sort-header class="ArFontStyle">
            {{ lang == 'en' ? formBodyLabels[0].lblEnglishName1.title : formBodyLabels[1].lblEnglishName1.title}}
          </th>
          <td mat-cell *matCellDef="let item" class="w-10 ArFontStyle">
            {{item.refname1}}
          </td>
        </ng-container>
        <ng-container matColumnDef="arabicName2">
          <th mat-header-cell *matHeaderCellDef mat-sort-header class="ArFontStyle">
            {{ lang == 'en' ? formBodyLabels[0].lblArabicName2.title : formBodyLabels[1].lblArabicName2.title}}
          </th>
          <td mat-cell *matCellDef="let item" class="w-10 ArFontStyle">
            {{item.refname1}}
          </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="columnsToDisplay; sticky:true"
            [ngClass]="{'hidden':!isLoadingCompleted}" class="setup-search-header"></tr>
        <tr mat-row *matRowDef="let refTableDto; columns: columnsToDisplay"></tr>
      </table>
    </div>
    <div [ngClass]="{'hidden':!isLoadingCompleted}">
      <mat-paginator [pageSize]="10" [pageSizeOptions]="[10,20,30]" (page)="onPaginationChange($event)" showFirstLastButtons>
      </mat-paginator>
    </div>
  </div>
  <!--begin::Body-->
  <div class="card-footer">

  </div>
</div>
<!--Delete Modal-->
<ng-template #deleteModal let-modal>
  <div class="modal-header">
    <h4 class="modal-title" id="modal-basic-title">  {{ lang == 'en' ? formBodyLabels[0].lblDelete.title : formBodyLabels[1].lblDelete.title}}</h4>
    <button type="button" class="btn-close" aria-label="Close" (click)="modal.dismiss('Cross click')"></button>
  </div>
  <div class="modal-body">
    <form>
      <div class="mb-3">
        <span>{{ lang == 'en' ? formBodyLabels[0].lblDeleteMsg.title : formBodyLabels[1].lblDeleteMsg.title}}</span>
      </div>
    </form>
  </div>
  <div class="modal-footer text-right">
    <button mat-raised-button color="warn" (click)="modal.close('yes')">{{ lang == 'en' ? formBodyLabels[0].lblYes.title : formBodyLabels[1].lblYes.title}}</button> &nbsp;&nbsp;
    <button mat-raised-button color="accent" (click)="modal.close('Save click')">{{ lang == 'en' ? formBodyLabels[0].lblNo.title : formBodyLabels[1].lblNo.title}}</button>
  </div>
</ng-template>

<!--Add New Record-->
<ng-template #mymodal let-modal>
  <div class="modal-header bg-light">
    <button type="button" class="close" data-dismiss="modal" (click)="modal.dismiss('Cross click')">&times;</button>
