export class AddemployeeinformationComponent {
  constructor(cdr, employeeService, toastrService, commonDbService, common, fb, activatedRout, router, modalService, renderer, userFunctionService, auth) {
    this.cdr = cdr;
    this.employeeService = employeeService;
    this.toastrService = toastrService;
    this.commonDbService = commonDbService;
    this.common = common;
    this.fb = fb;
    this.activatedRout = activatedRout;
    this.router = router;
    this.modalService = modalService;
    this.renderer = renderer;
    this.userFunctionService = userFunctionService;
    this.auth = auth;
    this.isLoading$ = new BehaviorSubject(false);
    this.unsubscribe = []; //

    this.isChildFormSet = false;
    this.showChildComponent = false; //
    //

    this.isFormSubmitted = false; // We will get form lables from lcale storage and will put into array.

    this.AppFormLabels = []; // We will filter form header labels array

    this.formHeaderLabels = []; // We will filter form body labels array

    this.formBodyLabels = {
      en: {},
      ar: {}
    };
    this.maritalStatusArray = [{
      id: 1,
      name: 'Married'
    }, {
      id: 2,
      name: 'Single'
    }];
    this.genderArray = [{
      id: 1,
      name: 'Male'
    }, {
      id: 2,
      name: 'Female'
    }];
    this.closeResult = '';
    this.isOK = false;
    this.isManager = false;
    this.datePickerConfig = Object.assign({}, {
      containerClass: 'theme-dark-blue'
    });
    const loadingSubscr = this.isLoading$.asObservable().subscribe(res => this.isLoading = res);
    this.unsubscribe.push(loadingSubscr);
    this.setUpParentForm(); //

    this.employeeId = this.activatedRout.snapshot.paramMap.get('employeeId');
    this.countries$ = this.commonDbService.GetCountryList();
  }

  ngOnInit() {
    var _a;

    this.common.getLang().subscribe(lang => {
      this.lang = lang;
    });
    this.initializeForm(); //

    this.initializeJobDetailsForm(); //

    this.initializeMembershipForm(); //

    this.initPopUpModal(); //#region TO SETUP THE FORM LOCALIZATION    
    // TO GET THE LANGUAGE ID e.g. 1 = ENGLISH and 2 =  ARABIC

    this.languageType = localStorage.getItem('langType'); // To get the selected language...

    this.language = localStorage.getItem('lang'); // To setup the form id so will will get form labels based on form Id

    this.formId = 'AddEmployee'; // Check if LocalStorage is Not NULL

    if (localStorage.getItem('AppLabels') != null) {
      // Get data from LocalStorage
      this.AppFormLabels = JSON.parse(localStorage.getItem('AppLabels') || '{}');

      for (let labels of this.AppFormLabels) {
        if (labels.formID == this.formId) {
          this.formHeaderLabels.push(labels);
          const jsonFormTitleDTLanguage = labels.formTitleDTLanguage.reduce((result, element) => {
            result[element.labelId] = element;
            return result;
          }, {});

          if (labels.language == 1) {
            this.formBodyLabels['en'] = jsonFormTitleDTLanguage;
          } else if (labels.language == 2) {
            this.formBodyLabels['ar'] = jsonFormTitleDTLanguage;
          } // this.formBodyLabels.push(jsonFormTitleDTLanguage);


          console.log(this.formHeaderLabels);
          console.log(this.formBodyLabels);
        }
      }
    } //#endregion
    //#region Filling All dropDown from db    
    // To FillUp Occupations


    this.occupations$ = this.commonDbService.GetOccupations(); // To FillUp Departments

    this.departments$ = this.commonDbService.GetDepartments(); // To FillUp terminations

    this.terminations$ = this.commonDbService.GetTerminations(); // To FillUp Contract Types

    this.contractType$ = this.commonDbService.GetContractType();
    this.getLocations(); //#endregion
    // Get and fill data in Edit Mode...

    if (this.employeeId != null) {
      this.jobDetailsForm.controls.empCidNum.disable();
      this.jobDetailsForm.controls.salary.disable();
      this.isOK = true;
