export class FinancialApprovalComponent {
  constructor(financialService, router, commonService, fb, datepipe, modalService, toastrService, employeeService) {
    this.financialService = financialService;
    this.router = router;
    this.commonService = commonService;
    this.fb = fb;
    this.datepipe = datepipe;
    this.modalService = modalService;
    this.toastrService = toastrService;
    this.employeeService = employeeService; //#region
