    this.columnsToDisplay = ['action', 'transId', 'employee', 'mobile', 'service']; // We need a normal array of data so we will subscribe to the observable and will get data

    this.financialApprovalDto = new MatTableDataSource([]); // Hide footer while loading.

    this.isLoadingCompleted = false; // Incase of any error will display error message.

    this.dataLoadingStatus = ''; // True of any error

    this.isError = false; // Search Term

    this.searchTerm = '';
    this.closeResult = ''; //

    this.currentPage = 0;
    this.totalRows = 0;
