    this.formBodyLabels = [];
    this.modalLabels = [];
    this.selectedBtn = 'jvView';
    this.formGroup = new FormGroup({
      searchTerm: new FormControl("")
    });
    var data = JSON.parse(localStorage.getItem("user"));
    this.tenentId = data.map(obj => obj.tenantId);
    this.locationId = data.map(obj => obj.locationId);
    this.periodCode = data.map(obj => obj.periodCode);
    this.prevPeriodCode = data.map(obj => obj.prevPeriodCode);
    this.roleId = data.map(obj => obj.roleId);
    this.username = data.map(obj => obj.username);
    this.userParams = this.financialService.getUserParams();
  }

  ngOnInit() {
    //
    this.loadData(0); //

    this.initApproveServiceForm(); //

    this.initRejectionServiceForm(); //

    this.rejectionType$ = this.financialService.GetRejectionType(); //

    this.initEmployeeForm();
    this.commonService.getLang().subscribe(lang => {
      this.lang = lang;
    });
    this.formId = 'FinancialApprovals';
    var modalId = 'voucherModal'; // Check if LocalStorage is Not NULL

    if (localStorage.getItem('AppLabels') != null) {
      // Get data from LocalStorage
      this.AppFormLabels = JSON.parse(localStorage.getItem('AppLabels') || '{}');

      for (let labels of this.AppFormLabels) {
        if (labels.formID == this.formId) {
          this.formHeaderLabels.push(labels);
          const jsonFormTitleDTLanguage = labels.formTitleDTLanguage.reduce((result, element) => {
            result[element.labelId] = element;
            return result;
          }, {});
          this.formBodyLabels.push(jsonFormTitleDTLanguage);
        }

        if (labels.formID == modalId) {
          const jsonFormTitleDTLanguage = labels.formTitleDTLanguage.reduce((result, element) => {
            result[element.labelId] = element;
            return result;
          }, {});
          this.modalLabels.push(jsonFormTitleDTLanguage);
        }
      }

      console.log(this.formBodyLabels);
      console.log(this.formHeaderLabels);
    }
  }

